// @import url('https://fonts.googleapis.com/css?family=Playfair+Display|Source+Sans+Pro:200,400');

import styled from "styled-components";

const Title = styled.h1`
  font-size: 4rem;
  padding-bottom: 1rem;
`;

/* Styles for the hero image */
const Hero = styled.section`
  background: url("https://images.unsplash.com/photo-1518176258769-f227c798150e")
    center;
  background-size: cover;
  padding: 4rem 2rem;
  /* grid styles */
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
  align-items: center;
  color: white;
`;

const Description = styled.article`
  font-size: 1.5rem;
  font-weight: 200;
`;

const Link = styled.a`
  padding: 1rem;
  margin-top: 0.75rem;
  background-color: goldenrod;
  text-decoration: none;
  color: white;
  border-radius: 0.25rem;
  text-align: center;
  display: inline-block;
  transition: all 0.3s;
  &:hover {
    opacity: 0.6;
  }
`;

const Receipes = styled.ul`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
  grid-gap: 1rem;
  list-style: none;
`;

const Food = styled.li`
  border: 1px solid #e2e2e2;
  border-radius: 0.5rem;
  padding: 2rem;
`;

const PhotoPos = styled.figure`
  max-height: 220px;
  overflow: hidden;
  border-top-left-radius: 0.5rem;
  border-top-right-radius: 0.5rem;
  position: relative;
`;

const Photo = styled.img`
  width: 100%;
`;

const Summary = styled.figcaption`
  position: absolute;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.7);
  width: 100%;
`;

const Name = styled.h3`
  color: white;
  padding: 0.75rem;
  font-size: 1.25rem;
`;
const Text = styled.p`
  font-size: 1rem;
  line-height: 1.5;
  padding: 1rem 0.75rem;
  color: #666666;
`;

const RecLink = styled.a`
  padding: 0.5rem 1rem;
  margin: 0.5rem;
`;

const Footer = styled.footer`
  background-color: #333;
  padding: 0.75rem;
  color: white;
  text-align: center;
  font-size: 0.75rem;
`;

export default function Index() {
  return (
    <div>
      <main className="wrapper">
        <Hero>
          <Title>You thirsty?</Title>
          <Description>
            <p>
              Explore local breweries with just one click and stirred by
              starlight across the centuries light years great turbulent clouds
              circumnavigated paroxysm of global death.
            </p>
            <a href="#breweries">Browse Breweries</a>
          </Description>
        </Hero>
        <section>
          <Receipes>
            <Food>
              <PhotoPos>
                <Photo
                  src="https://images.unsplash.com/photo-1471421298428-1513ab720a8e"
                  alt="Several hands holding beer glasses"
                />
                <Summary>
                  <Name>Billions upon billions</Name>
                </Summary>
              </PhotoPos>
              <Text>
                Made in the interiors of collapsing stars star stuff harvesting
                star light venture billions upon billions Drake Equation brain
                is the seed of intelligence?
              </Text>
              <RecLink href="#">Visit Website</RecLink>
            </Food>
            <Food>
              <PhotoPos>
                <Photo
                  src="https://images.unsplash.com/photo-1513309914637-65c20a5962e1?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=3450&q=80"
                  alt="Several friends doing a toast"
                />
                <Summary>
                  <Name>Drake Equation</Name>
                </Summary>
              </PhotoPos>
              <Text>
                Another world citizens of distant epochs from which we spring
                descended from astronomers Orion's sword shores of the cosmic
                ocean.
              </Text>
              <RecLink href="#">Visit Website</RecLink>
            </Food>
            <Food>
              <PhotoPos>
                <Photo
                  src="https://images.unsplash.com/photo-1535359056830-d4badde79747?ixlib=rb-1.2.1&auto=format&fit=crop&w=3402&q=80"
                  alt="Three different glasses of beer"
                />
                <Summary>
                  <Name>Vast cosmic arena</Name>
                </Summary>
              </PhotoPos>
              <Text>
                Galaxies the ash of stellar alchemy prime number science
                inconspicuous motes of rock and gas brain is the seed of
                intelligence.
              </Text>
              <RecLink href="#">Visit Website</RecLink>
            </Food>
          </Receipes>
        </section>
      </main>

      <Footer>
        <p>&copy; 2019. Made with ❤ and CSS Grid.</p>
      </Footer>
    </div>
  );
}
