import styled from "styled-components";
import Link from "next/link";

const Title = styled.h1`
  font-size: 4rem;
  padding: 8%;
  font-family: Montserrat;
  font-weight: 900;
`;

const Hero = styled.section`
  background: white;
  background-size: cover;
  padding: 10% 8%;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
  align-items: center;
  color: black;
`;

const Description = styled.article`
  font-size: 1.8rem;
  font-weight: 200;
  font-family: Lato;
`;

const Browse = styled.a`
  padding: 0.75rem;
  margin-top: 0.75rem;
  background-color: goldenrod;
  text-decoration: none;
  color: white;
  border-radius: 0.25rem;
  text-align: center;
  display: inline-block;
  transition: all 0.3s;
  font-size: 1.4rem;
  font-family: Lato;
  &:hover {
    opacity: 0.6;
  }
`;

const Receipes = styled.ul`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
  grid-gap: 4rem;
  list-style: none;
  margin: 10% 8%;
`;

const Food = styled.li`
  border: 1px solid #e2e2e2;
  border-radius: 0.5rem;
  //   padding: 1rem;
`;

const PhotoPos = styled.figure`
  max-height: 220px;
  overflow: hidden;
  position: relative;
`;

const Photo = styled.img`
  width: 100%;
`;

const Summary = styled.figcaption`
  position: absolute;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.7);
  width: 100%;
`;

const Name = styled.h3`
  color: white;
  padding: 0.75rem;
  font-size: 1.25rem;
  font-family: Lato;
  font-weight: 200;
`;
const Text = styled.p`
  font-size: 1rem;
  line-height: 1.5;
  padding: 2rem;
  color: #666666;
  font-family: Lato;
  font-weight: 200;
`;

const RecLink = styled(Browse)`
  padding: 0.5rem 1rem;
  margin: 2rem;
`;

const FoodLink = props => (
  <Link href="/p/[id]" as={`/p/${props.id}`}>
    <RecLink>Get recipe</RecLink>
  </Link>
);

export default function Homepage() {
  return (
    <main>
      <Hero>
        <Title>
          Guilt Free <br /> Guilty Pleasures
        </Title>
        <Description>
          <p>Vegetarian comfort food.</p>
          <Browse href="/recipes">Browse Recipes</Browse>
        </Description>
      </Hero>
      <section>
        <Receipes>
          <Food>
            <PhotoPos>
              <Photo
                src="https://images.unsplash.com/photo-1471421298428-1513ab720a8e"
                alt="Several hands holding beer glasses"
              />
              <Summary>
                <Name>Billions upon billions</Name>
              </Summary>
            </PhotoPos>
            <Text>
              Made in the interiors of collapsing stars star stuff harvesting
              star light venture billions upon billions Drake Equation brain is
              the seed of intelligence?
            </Text>
            <FoodLink id={"Billions upon billions"} />
          </Food>
          <Food>
            <PhotoPos>
              <Photo
                src="https://images.unsplash.com/photo-1513309914637-65c20a5962e1?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=3450&q=80"
                alt="Several friends doing a toast"
              />
              <Summary>
                <Name>Drake Equation</Name>
              </Summary>
            </PhotoPos>
            <Text>
              Another world citizens of distant epochs from which we spring
              descended from astronomers Orion's sword shores of the cosmic
              ocean.
            </Text>
            <RecLink>Get recipe</RecLink>
          </Food>
          <Food>
            <PhotoPos>
              <Photo
                src="https://images.unsplash.com/photo-1535359056830-d4badde79747?ixlib=rb-1.2.1&auto=format&fit=crop&w=3402&q=80"
                alt="Three different glasses of beer"
              />
              <Summary>
                <Name>Vast cosmic arena</Name>
              </Summary>
            </PhotoPos>
            <Text>
              Galaxies the ash of stellar alchemy prime number science
              inconspicuous motes of rock and gas brain is the seed of
              intelligence.
            </Text>
            <RecLink>Get recipe</RecLink>
          </Food>
        </Receipes>
      </section>
    </main>
  );
}
